// import React from 'react';
import React, { useState } from 'react';
import m1 from "./images/m1.jpg";
import m2 from "./images/m2.jpg";
import m3 from "./images/m3.jpg";
import m4 from "./images/m4.jpg";
import m5 from "./images/m5.jpg";
import l1 from "./images/l1.jpg";
import l2 from "./images/l2.jpg";
import l3 from "./images/l3.jpg";
import l4 from "./images/l4.jpg";
import l5 from "./images/l5.jpg";
import l6 from "./images/l6.jpg";




function Home() {
  let [count,setc]=useState(0)
  const arr=[
    {id:0,i:m1,n:"GAlaxy A50",b:"SAMSUNG",p:109.99},
    {id:1,i:m3,n:"F 17",b:"OPPO",p:100.49},
    {id:2,i:l2,n:"Mac 14",b:"APPLE",p:139.39},
    {id:3,i:m4,n:"B 21",b:"OPPO",p:208.23},
    {id:4,i:l3,n:"M1x40",b:"DELL",p:136.65},
    {id:5,i:m5,n:"Gen A12",b:"I BALL",p:190.22},
    {id:6,i:m1,n:"GAlaxy A30",b:"SAMSUNG",p:202.91},
    {id:7,i:m2,n:"F 19",b:"OPPO",p:344.09},
    {id:8,i:l1,n:"X50",b:"ASUS",p:125.11},
    {id:9,i:l4,n:"WZ12X",b:"HP",p:256.27}
  ]
  const [cart,updateCart]=useState([])
  function side_cart(){
    const c=document.getElementById("cart");
    const m=document.getElementById("main");
    m.style.width="85%";
    c.style.right=0;
  }
  function close(){
    const c=document.getElementById("cart");
    const m=document.getElementById("main");
    m.style.width="100%";
    c.style.right="-340px";
  }
  function increse(id){
    const c=[...cart]

    for(let i=0;i<c.length;i++){
      if(id==cart[i].pro.id){
        c[i].quantity++;
        updateCart(c)
        return;
      }
    }
  }
  function icrese(id){
    const c=[...cart]

    for(let i=0;i<c.length;i++){
      if(id==cart[i].pro.id){
        if(c[i].quantity<1){
          c.splice(i,1);
          updateCart(c)
        }
        c[i].quantity--;
        updateCart(c)
        return;
      }
    }
  }
  function add(id){
    setc(count+1)
    const c=[...cart]

    for(let i=0;i<c.length;i++){
      if(id==cart[i].pro.id){
        c[i].quantity++;
        updateCart(c)
        return;
      }
    }


    let arr_item={
      pro:arr[id],
      quantity:1
    }
    c.push(arr_item)
    updateCart(c)
    console.log(cart);
  }
  
  return (
    <>
    <div className='main' id="main">
      {arr.map((p)=>{return (<div class="card">
        <Product key={p.id} image={p.i} brand={p.b} name={p.n} price={p.p}/>
        <p><button onClick={()=>add(p.id)}>Add to Cart</button></p>
</div>)
        } )}
    </div>
    <button className='cart' onClick={side_cart}>CART <sup><strong>{count}</strong></sup></button>
    <div className="side-cart" id='cart'>
    <div className="cart-header">
      <h2>Shopping Cart</h2>
      <button class="close-btn" onClick={close}>&times;</button>
    </div>
    <div className="cart-items">
      {cart.map((p)=>{ 
        return (<div className='cart-item'><Cart_items image={p.pro.i} name={p.pro.n} brand={p.pro.b} price={p.pro.p} />
        <div className="quantity">
      <button className="quantity-btn minus-btn">-</button>
      <span className="quantity-value">{p.quantity}</span>
      <button className="quantity-btn plus-btn" onClick={()=>increse(p.pro.id)}>+</button>
    </div>
  <button className="remove-btn">Remove</button>
  </div>
        )
        })}
      </div>
    <div className="cart-footer">
      <p>Total: $50</p>
      <button className="checkout-btn">Checkout</button>
    </div>
  </div>
    
    </>
  )
}

function Product(props){
  return (
    <>
  <img src={props.image} height="60%" width="60%" className='image'/>
  <h3 className="name">{props.brand} {props.name}</h3>
  <p class="price">{props.price} $</p>
  <p  class="detail">{props.detail}</p>
    </>
  )
}

function Cart_items(props){

  return(
<div className='cart-item'>
  <div className="product-details">
  <img src={props.image} alt="Product Name" />
    <h3>{props.brand} <br></br>{props.name}</h3>
    <p>Price: {props.price}$</p>
  </div>
  </div>
      
  )
}

export default Home
