        <h2>Sign Up</h2>
        <input id="name"  type="text" placeholder="Full Name" required/>
        <input id="email" type="email" placeholder="Email" required/>
        <input id="pass" type="password" placeholder="Password" required/>
        <button type="submit">Sign Up</button>
      </form>
    </div>
