import React from 'react'
import {arr} from './Home'

function About() {
  function func(){
  console.log(arr);
  const categoryRadios = document.querySelectorAll('input[name="category"]');
const featureCheckboxes = document.querySelectorAll('input[name="feature"]');
const productList = document.querySelector('.product-list');

function applyFilters() {
    const selectedCategory = document.querySelector('input[name="category"]:checked').value;
    const selectedFeatures = Array.from(featureCheckboxes)
        .filter(checkbox => checkbox.checked)
        .map(checkbox => checkbox.value);

    // You would typically fetch and update the product list based on the selected filters here
    // For this example, let's just log the selected filters
    console.log('Selected Category:', selectedCategory);
    console.log('Selected Features:', selectedFeatures);
}

categoryRadios.forEach(radio => radio.addEventListener('change', applyFilters));
featureCheckboxes.forEach(checkbox => checkbox.addEventListener('change', applyFilters));

  }
  return (
   <>
   {/* <h1>ABOUT</h1> */}
   <div className="filter-menu">
        <h2>Filter Menu</h2>
        
        <div className="filter-section">
            <h3>Category</h3>
            <label><input type="radio" name="category" value="all" checked/> All</label>
            <label><input type="radio" name="category" value="clothing"/> Clothing</label>
            <label><input type="radio" name="category" value="electronics"/> Electronics</label>
        </div>
        
        <div className="filter-section">
            <h3>Features</h3>
            <label><input type="checkbox" name="feature" value="free-shipping"/> Free Shipping</label>
            <label><input type="checkbox" name="feature" value="discount"/> Discount</label>
        </div>
    </div>
   </>
  )
}

export default About
