import React from 'react';
import m1 from "./images/m1.jpg";
import m2 from "./images/m2.jpg";
import m3 from "./images/m3.jpg";
import m4 from "./images/m4.jpg";
import m5 from "./images/m5.jpg";
import l1 from "./images/l1.jpg";
import l2 from "./images/l2.jpg";
import l3 from "./images/l3.jpg";
import l4 from "./images/l4.jpg";
import l5 from "./images/l5.jpg";
import l6 from "./images/l6.jpg";

class product{
constructor(id,i,n,b,p,d){
  this.id=id;
  this.i=i;
  this.n=n;
  this.b=b;
  this.p=p;
  this.d=d;
}
}


function Home() {
  const arr=[
    {id:0,i:m1,n:"GAlaxy A50",b:"SAMSUNG",p:109.99},
    {id:1,i:m3,n:"F 17",b:"OPPO",p:100.49},
    {id:2,i:l2,n:"Mac 14",b:"APPLE",p:139.39},
    {id:3,i:m4,n:"B 21",b:"OPPO",p:208.23},
    {id:4,i:l3,n:"M1x40",b:"DELL",p:136.65},
    {id:5,i:m5,n:"Gen A12",b:"I BALL",p:190.22},
    {id:6,i:m1,n:"GAlaxy A30",b:"SAMSUNG",p:202.91},
    {id:7,i:m2,n:"F 19",b:"OPPO",p:344.09},
    {id:8,i:l1,n:"X50",b:"ASUS",p:125.11},
    {id:9,i:l4,n:"WZ12X",b:"HP",p:256.27}
  ]

  function side_cart(){
  const c=document.getElementById("cart");
  c.style.right=0;
  }
  function side_car(){
    const c=document.getElementById("cart");
    c.style.right=0;
    }
  
  return (
    <>
    <div className='main'>
      {arr.map((p)=><Product key={p.id} image={p.i} brand={p.b} name={p.n} price={p.p} />)}
    </div>
    <button className='cart' onClick={side_cart}>CART</button>
    <div className="side-cart" id='cart'>
    <div className="cart-header">
      <h2>Shopping Cart</h2>
      <button class="close-btn" onClick={close}>&times;</button>
    </div>
    <div className="cart-items">
      </div>
    <div className="cart-footer">
      <p>Total: $50</p>
      <button className="checkout-btn">Checkout</button>
    </div>
  </div>
    
    </>
  )
}

function Product(props){
  return (
    <>
    <div class="card">
  <img src={props.image} height="60%" width="60%" className='image'/>
  <h3 className="name">{props.brand} {props.name}</h3>
  <p class="price">{props.price} $</p>
  <p  class="detail">{props.detail}</p>
  <p><button>Add to Cart</button></p>
</div>
    </>
  )
}

export default Home
